import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import DatePicker from './DatePicker'
import Button from "@material-ui/core/Button";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    marginTop: 40
  },
  block: {
    display: "block",
    margin: 20,
  }
}));

export default function NewTrial() {
  const [spacing, setSpacing] = React.useState(2);
  const [name, setName] = React.useState(0);
  const [startDate, setStartDate] = React.useState(0);
  const [endDate, setEndDate] = React.useState(0);
  const [startRegDate, setStartRegDate] = React.useState(0);
  const [endRegDate, setEndRegDate] = React.useState(0);
  const [maxPart, setMaxPart] = React.useState(0);
  const [price, setPrice] = React.useState(0);
  const classes = useStyles();

  const fetchInputs = e => {
    getDate()
  }

  const getDate = (date, purpose) => {
    date.toISOString()
    date.toLocaleDateString()
    const formatedDate = new Date(date).toLocaleDateString('fr-CA')
    switch (purpose) {
        case "startDate":
            setStartDate(date)
            break;
        case "endDate":
                setEndDate(date)
                break;
                case "startRegDate":
            setStartRegDate(date)
            break;
            case "endRegDate":
                setEndRegDate(date)
                break;

        default:
            break;
    }
  }

  return (
    <Grid container className={classes.root}>
      <Grid item xs={12}>
        <Grid container justify="center" spacing={spacing}>
            <Grid item>
                <TextField label="Name" onChange={e=> setName(e.currentTarget.value)} className={classes.block}/>
                <DatePicker purpose="startDate" label="Start date"/>
                <DatePicker getDate={getDate} purpose="endDate" label="End date"/>
                <DatePicker purpose="startRegDate" label="Start registration date"/>
                <DatePicker purpose="endRegDate" label="End registration date"/>
                <TextField label="Maximal partipians" onChange={e=> setMaxPart(e.currentTarget.value)} type="number" className={classes.block}/>
                <TextField label="Price in EUR" onChange={e=> setPrice(e.currentTarget.value)} type="number" className={classes.block}/>
                <Button
            onClick={fetchInputs}
            color="primary"
            variant="contained"

          >
            Submit
          </Button>
            </Grid>

        </Grid>
      </Grid>
    </Grid>
  );
}
