import React, {useState, useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import TextField from '@material-ui/core/TextField';
import BasicDatePickers from './DatePicker'
import Grid from '@material-ui/core/Grid';


const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
    },
  },
}));

export default function EditTrialForm(props) {
    const [name, setName] = useState()
    const [regStartDate, setRegStartDate] = useState()
    const classes = useStyles();


    const getDate = date => {
        const newDate = date.toISOString()
        console.log(newDate)
    }

  return (
        <Grid container justify="space-around">
    <form justify="space-around"  className={classes.root} noValidate autoComplete="off">
      <TextField id="standard-secondary" label={props.trial.name} onChange={e=> setName(e.target.value)} color="primary" />
        <BasicDatePickers 
            label="Registration start date" 
            trial={props.trial.registrationStartDate} 
            getDate={getDate}/>
        <BasicDatePickers 
            label="Registration end date" 
            trial={props.trial.registrationStartDate} 
            getDate={getDate}/>
    </form>
        </Grid>
  );
}
