import React, { useState, useEffect } from "react";
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

function EnrollDecide(props) {
    const style= {
        margin:40,
        display: "flex",
        justifyItems: "spaceBetween"
    }
  return (
    <div>
      {props.enrollment.map((enroll) => {
        if (enroll.status == "pending") {
          const usrEmail = props.user.find((usr) => usr.id === enroll.userId);
          const trialName = props.trial.find((el) => el.id === enroll.trialId);
          return (
            <Grid item xs={3}>
            <Paper className={classes.paper}>
            <p >{trialName.name}</p>
            <p >{usrEmail.email}</p>
            <p >{enroll.status}</p>

            </Paper>
          </Grid>
          );
        }
      })}
    </div>
  );
}

export default EnrollDecide;
