import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Button from "@material-ui/core/Button";

const useStyles = makeStyles({
  table: {
    minWidth: 650,
    marginTop: 80,
  },
});

export default function EnrollDecide(props) {
  const classes = useStyles();


  const submitDecide = e => {
      console.log(e.currentTarget.value)
      console.log(e.currentTarget.dataset.enrollid)
  }

  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} size="small" aria-label="a dense table">
        <TableHead>
          <TableRow>
            <TableCell>Email</TableCell>
            <TableCell align="right">Trial</TableCell>
            <TableCell align="right">Status</TableCell>
            <TableCell align="right">Action</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {props.enrollment.map((enroll) => {
            if (enroll.status == "pending") {
              const usrEmail = props.user.find(
                (usr) => usr.id === enroll.userId
              );
              const trialName = props.trial.find(
                (el) => el.id === enroll.trialId
              );
              return (
                <TableRow key={trialName.name}>
                  <TableCell align="right">{trialName.name}</TableCell>
                  <TableCell align="right">{usrEmail.email}</TableCell>
                  <TableCell align="right">{enroll.status}</TableCell>
                  <TableCell align="right">
                    <Button data-enrollid={enroll.id} onClick={submitDecide} value="approved" size="small" variant="outlined" color="primary">
                      Approve
                    </Button>
                    <Button data-enrollid={enroll.id} onClick={submitDecide} value="cancelled" size="small" variant="outlined" color="primary">
                      Cancel    
                    </Button>
                    <Button data-enrollid={enroll.id} onClick={submitDecide} value="rejected" size="small" variant="outlined" color="primary">
                      Reject   
                    </Button>
                  </TableCell>
                </TableRow>
              );
            }
          })}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
