import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import BasicDatePickers from "./DatePicker";
import Grid from "@material-ui/core/Grid";
import DialogActions from "@material-ui/core/DialogActions";
import Button from "@material-ui/core/Button";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
}));

export default function EditTrialForm(props) {
  const [name, setName] = useState();
  const [regStartDate, setRegStartDate] = useState();
  const [regEndDate, setRegEndDate] = useState();
  const classes = useStyles();

  const getDate = (date, start) => {
    const newDate = date.toISOString();
    if (start) {
      setRegStartDate(newDate);
    } else setRegEndDate(newDate);
  };

  const submitEditForm = () => {
    // console.log(name);
    console.log(regStartDate || props.trial.registrationStartDate);
  };

  return (
    <form
      justify="space-around"
      className={classes.root}
      noValidate
      autoComplete="off"
    >
      <Grid container justify="space-around">
        <TextField
          id="standard-secondary"
          label={props.trial.name}
          value={name}
          onChange={(e) => setName(e.target.value)}
          color="primary"
        />
        <BasicDatePickers
          start={true}
          label="Registration start date"
          trial={props.trial.registrationStartDate}
          getDate={getDate}
        />
        <BasicDatePickers
          start={false}
          label="Registration end date"
          trial={props.trial.registrationStartDate}
          getDate={getDate}
        />
        <DialogActions>
          <Button onClick={props.handleClose} color="primary">
            Cancel
          </Button>
          <Button
            onClick={props.handleClose}
            color="primary"
            variant="contained"
            onClick={submitEditForm}
          >
            Submit
          </Button>
        </DialogActions>
      </Grid>
    </form>
  );
}
