import React, { useState, useEffect } from "react";
import "./App.css";
import axios from "axios";

import Login from "./Views/Login";
import Admin from "./Views/Admin";
import User from "./Views/User";

function App() {
  const [roleMapping, setRoleMapping] = useState({});
  const [role, setRole] = useState({});
  const [whoIsLogged, setWhoIsLogged] = useState(0);
  const [users, setUsers] = useState([]);

  const fetch = async (url, sets) => {
    const result = await axios(url);
    sets(result.data);
  };

  useEffect(() => {
    fetch("http://localhost:3000/role", setRole);
    fetch("http://localhost:3000/roleMapping", setRoleMapping);
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    const userInput = e.target.dataset.value;
    await fetch("http://localhost:3000/user", await setUsers)
    .then(checkUser(users, roleMapping, userInput))
  };


  //checking if user exist and parsing the role
  const checkUser = (users, rolemap, userInput) => {
    users.map(user => {
      if(user.email === userInput)(
        console.log(user.email)
        )
      }
    )
  }

  // based on whoislogged int it will render view
  let view;
  if (whoIsLogged === 0) {
    view = <Login submit={submit} />;
  } else if (whoIsLogged === 1) {
    view = <Admin />;
  } else view = <User />;

  return <div className="App">{view}</div>;
}

export default App;
