import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Input from "@material-ui/core/Input";
import TextField from "@material-ui/core/TextField";
import BasicDatePickers from "./DatePicker";
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
}));

export default function EditTrialForm(props) {
  const [name, setName] = useState();
  const [regStartDate, setRegStartDate] = useState();
  const [regEndDate, setRegEndDate] = useState();
  const classes = useStyles();

  const getDate = (date, start) => {
    const newDate = date.toISOString();
    if(start){
        setRegStartDate(newDate)
    }else setRegEndDate(newDate)
  };

  return (
    <form
      justify="space-around"
      className={classes.root}
      noValidate
      autoComplete="off"
    >
      <Grid container justify="space-around">
        <TextField
          id="standard-secondary"
          label={props.trial.name}
          onChange={(e) => setName(e.target.value)}
          color="primary"
        />
        <BasicDatePickers
          start={true}
          label="Registration start date"
          trial={props.trial.registrationStartDate}
          getDate={getDate}
        />
        <BasicDatePickers
          start={false}
          label="Registration end date"
          trial={props.trial.registrationStartDate}
          getDate={getDate}
        />
      </Grid>
    </form>
  );
}
