import React, { useState, useEffect } from "react";
import axios from "axios";
import Card from "./Card";

function TrialList(props) {
  const loggedUser = props.user
  const [trials, setTrials] = useState([]);
  const [enrollment, setEnrollment] = useState([]);
  const [enrolled, setEnrolled] = useState([]);
  const [howManyEnrolled, setHowManyEnrolled] = useState([]);

  const fetch = async (url, sets) => {
    const result = await axios(url);
    sets(result.data);
  };

  //Fetch trials and enrollments
  useEffect(() => {
    fetch("http://localhost:3000/Trial", setTrials);
    fetch("http://localhost:3000/Enrollment", setEnrollment);
  }, []);


  //calculate how many are enrollments 


  useEffect(() => {
    const howManyEnrolls = () => {
      const totalCount = []
      for (let index = 0; index < enrollment.length; index++) {
          for (let i = 0; i < totalCount.length; index++) {
            if(totalCount[i].id === enrollment[index].id){
              totalCount[i].count =+ 1
            }else totalCount.push({id: enrollment[index].id, count: 1}) 
          }
      }
      setHowManyEnrolled(totalCount)
    }
  }, [enrollment]);
  console.log(howManyEnrolled)

  //when enrollments is fetched filter enrollments that this user participate
  //and store in @enrolled
  useEffect(() => {
    let enrolledTrials = [];
    for (let index = 0; index < enrollment.length; index++) {
      if (enrollment[index].userId == loggedUser) {
        enrolledTrials.push(enrollment[index].id);
      }
    }
    setEnrolled(enrolledTrials)
  }, [enrollment]);


  return (
    <div>
      {
        trials.map((trial) => (
          <p>a</p>
          //<Card key={trial.id} {...trial} isEnrolled={enrolled.includes(trial.id)}/>
          ))}
    </div>
  );
}

export default TrialList;
