import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';


import Login from './Views/Login'
import Admin from './Views/Admin'
import User from './Views/User'

function App() {
  const [roleMapping, setRoleMapping] = useState({});
  const [role, setRole] = useState({});
  const [whoIsLogged, setWhoIsLogged] = useState(0)


  useEffect(() => {
    const fetch = async (url, sets) => {
      const result = await axios(url);
      return sets(result.data)
    };
      fetch('http://localhost:3000/Role', setRole);
      fetch('http://localhost:3000/RoleMapping', setRoleMapping);
    },[])

    let view;
    if(whoIsLogged === 0) {
      view = <Login />
    }else if(whoIsLogged === 1) {
      view = <Admin />
    }

    return (
    <div className="App">
      {view}

    </div>
  );
}

export default App;
